class Book {
  // 생성자 함수
  constructor(
    title,
    link,
    image,
    author,
    discount,
    publisher,
    isbn,
    description,
    pubdate,
    price
  ) {
    this.title = title;
    this.link = link;
    this.image = image;
    this.author = author;
    this.discount = discount;
    this.publisher = publisher;
    this.isbn = isbn;
    this.description = description;
    this.pubdate = pubdate;
    this.price = price;
  }
}
